const Node = ({datapoint, radii, distanceColors, superpermutationColors,
                  onCircleClick, onCircleDoubleClick,
                  activeDatapoint, activeSuperpermutaton,
                  superpermutations}) => {

    function useSingleAndDoubleClick(actionSimpleClick, actionDoubleClick, datapoint, delay = 250) {
        const [click, setClick] = useState(0);

        useEffect(() => {
            const timer = setTimeout(() => {
                // simple click
                if (click === 1) actionSimpleClick(datapoint);
                setClick(0);
            }, delay);

            // the duration between this click and the previous one
            // is less than the value of delay = double-click
            if (click === 2) actionDoubleClick(datapoint);

            return () => clearTimeout(timer);

        }, [click]);

        return () => setClick(prev => prev + 1);
    }
    let clickAction = useSingleAndDoubleClick(onCircleClick, onCircleDoubleClick, datapoint)

    const x = radii[datapoint.D1T2-1] * Math.sin(2*Math.PI * datapoint.oc_order / 120) + 7 * Math.sin(2*Math.PI * datapoint.p_order / 7)
    const y = radii[datapoint.D1T2-1] * Math.cos(2*Math.PI * datapoint.oc_order / 120) + 7 * Math.cos(2*Math.PI * datapoint.p_order / 7)
    let fillColor = "white"
    let strokeColor = "black"
    const arr = []
    const arr2 = []
    const arr3 = ["1235746"]
    if (activeDatapoint !== null) {
        if (arr.includes(datapoint.permutation)) {
            fillColor = "#488c43"
        }
        else if (arr2.includes(datapoint.permutation)) {
            fillColor = "#684d0a"
        }
        else if (activeDatapoint.permutation === datapoint.permutation) {
            fillColor = distanceColors[0]
            // strokeColor = superpermutationColors[activeSuperpermutaton]
        }
        else if (arr3.includes(datapoint.permutation)) {
            fillColor = "#ff2626"
        }
        else if (activeDatapoint.DF1[0] === datapoint.permutation) {
            fillColor = distanceColors[1]
        }
        else if (activeDatapoint.DF2.includes(datapoint.permutation)) {
            fillColor = distanceColors[2]
        }
        else if (activeDatapoint.DB2.includes(datapoint.permutation)) {
            fillColor = "DeepSkyBlue"
        }
        else if (activeDatapoint.DF3.includes(datapoint.permutation)) {
            fillColor = distanceColors[3]
        }
        else if (activeDatapoint.DF4.includes(datapoint.permutation)) {
            fillColor = distanceColors[4]
        }
        else if (activeDatapoint.DF5.includes(datapoint.permutation)) {
            fillColor = distanceColors[5]
        }
        else if (activeDatapoint.DF6.includes(datapoint.permutation)) {
            fillColor = distanceColors[6]
        }
        else {
            fillColor = distanceColors[7]
        }
    }
    for (let i = 0; i < 3; i++) {
        if (superpermutations[i].includes(datapoint.permutation)) {
            strokeColor = superpermutationColors[i]
        }
    }

    return (
        <g className='node'
            onClick={clickAction}
        >
            <circle
                cx={x}
                cy={y}
                r="3px"
                fill={fillColor}
                stroke={strokeColor}
            />
            {datapoint.is_12p &&
                <circle
                cx={x}
                cy={y}
                r="4px"
                fill="none"
                stroke="purple"
                />
            }
        </g>
    );

}

const CentralCircle = ({radius}) => {
    return (
        <circle className='central-circle'
            cx="0"
            cy="0"
            r={`${radius}px`}
            fill="none"
            stroke="#eaeaea"
        />
    );

}

export default MyNetwork